import React from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'

import {
    getTotalBasketPrice,
    getBasketGoodsWithCount
} from '../../selectors'
import {
    removeGoodFromBasket,
    cleanBasket,
    basketCheckout
} from 'actions'
import {Link} from "react-router-dom";

const Basket = ({
                    goods,
                    totalPrice,
                    removeGoodFromBasket,
                    cleanBasket,
                    basketCheckout,
}) => {
    const isBasketEmpty = R.isEmpty(goods)
    const renderContent = () => (
        <div>
            {isBasketEmpty && <div>Ваша корзина пуста.</div>}
            <div className='table-responsive'>
                <table className='table-bordered table-striped table-condensed cf'>
                    <tbody>
                    {goods.map((good, index) =>(
                        <tr
                            key={index}
                            className='item-checout'
                        >
                            <td className='first-column-checkout'>
                                <img
                                    className='img-thumbnail'
                                    src={good.image}
                                    alt={good.name}
                                />
                            </td>
                            <td>{good.name}</td>
                            <td>₽{good.price}</td>
                            <td>{good.count}</td>
                            <td>
                                <span
                                    className='delete-cart'
                                    onClick={() => removeGoodFromBasket(good.id)}
                                ></span>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
            {R.not(isBasketEmpty) &&
                <div className='row'>
                    <div className='pull-right total-user-checkout'>
                        <b>Сумма: </b>
                        ₽{totalPrice}
                    </div>
                </div>
            }
        </div>
    )

    const renderSidebar = () => (
        <div>
            <Link
                to='/'
                className='btn btn-info'
            >
                <span className='glyphicon glyphicon-info-sign'></span>
                <span>Продолжить покупки</span>
            </Link>
                {R.not(isBasketEmpty)  &&
                    <div>
                        <button
                        onClick={cleanBasket}
                        className='btn btn-danger'
                        >
                            <span className='glyphicon glyphicon-trash'></span>
                            Очистить корзину
                        </button>
                        <button
                        className='btn btn-success'
                        onClick={() => basketCheckout(goods)}
                        >
                            <span className='glyphicon glyphicon-envelope'></span>
                            Оформить заказ
                        </button>
                    </div>
                }

        </div>
    )

    return(
        <div className='view-container'>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-9'>
                        {renderContent()}
                    </div>
                    <div className='col-md-3 btn-user-checkout'>
                        {renderSidebar()}
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        goods: getBasketGoodsWithCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

const mapDispatchToProps = {
    removeGoodFromBasket,
    cleanBasket,
    basketCheckout,
}

export default connect(mapStateToProps, mapDispatchToProps) (Basket)