import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {
    fetchGoods,
    loadMoreGoods,
    addGoodToBasket,
    fetchCategories
} from 'actions'
import {getGoods} from 'selectors'
import Layout from 'containers/layout'

class Goods extends Component {
    componentDidMount() {
        this.props.fetchGoods()
        this.props.fetchCategories()
    }

    renderGood(good, index) {
        // TODO добавить котроль длины наименования, аналогично краткому описанию
        const shortDescription = `${R.take(60, good.description)}...`
        const shortName = `${R.take(20, good.name)}`
        const {addGoodToBasket} = this.props
        return (
            <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
                <div className='thumbnail'>
                    <img className='img-thumbnail'
                         src={good.image}
                         alt={good.name}/>
                    <div className='caption'>
                        <span>
                        <h4 className='pull-right'>
                            ₽{good.price}
                        </h4>
                        <h4>
                            <Link to={`/goods/${good.id}`}>
                                {shortName}
                            </Link>
                        </h4>
                            </span>
                        <p>
                            {shortDescription}
                        </p>
                        <p className='itemButton'>
                            <button className='btn btn-primary'
                                    onClick={() => addGoodToBasket(good.id)}
                            >
                                Купить сейчас!
                            </button>
                            <Link to={`/goods/${good.id}`} className='btn btn-default'>
                                Информация.
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const {goods, loadMoreGoods} = this.props
        return (
            <Layout>
                <div className='books row'>
                    {goods.map((good, index) => this.renderGood(good, index))}
                </div>
                <div className='row'>
                    <div className='col-md-12'></div>
                    <button onClick={loadMoreGoods} className='pull-right btn btn-primary'>
                        Загрузить еще.
                    </button>
                </div>
            </Layout>
        )
    }
}

const mapStateToProps = (state, ownProps) => ({
    goods: getGoods(state, ownProps)
})

const mapDispatchToProps = {
    fetchGoods,
    loadMoreGoods,
    addGoodToBasket,
    fetchCategories,
}

export default connect(mapStateToProps, mapDispatchToProps)(Goods);